def poop( nurb_eyes=False ):
	bpy.ops.object.empty_add(type='SINGLE_ARROW')
	root = bpy.context.active_object
	root.location = [0,0,0]

	rig = {
		'EYES':[], 
		'root':root,
	}

	lattice_mods = []


	bmat = bpy.data.materials.new(name='black')
	bmat.use_nodes = False
	bmat.diffuse_color = [0,0,0, 1]
	bmat.roughness = 0
	bmat.zigzag_object_type = "EYES"

	wmat = bpy.data.materials.new(name='white')
	wmat.use_nodes = False
	wmat.diffuse_color = [1,1,1, 1]
	wmat.roughness = 0


	mmat = bpy.data.materials.new(name='mouth')
	mmat.use_nodes = False
	mmat.diffuse_color = [random()*0.2,0,0, 1]
	mmat.roughness = 0

	mouth = new_mesh('cylinder', vertices=8)
	mouth.rotation_euler.x = math.pi / 2
	mouth.scale = [1, 0.4, 1] #[0.5, 0.2, 0.5]
	mouth.location = [0,-1.32,0.26]
	mouth.data.materials.append(mmat)
	for poly in mouth.data.polygons:
		poly.material_index = 3
	for v in mouth.data.vertices:
		if v.co.y < 0:
			v.co.y = 0

	verts = []
	edges = []
	faces = []
	rad = 2.0
	for i in range(90*4):
		x = math.sin( math.radians(i*4) ) * rad
		y = math.cos( math.radians(i*4) ) * rad
		verts.append([x,y, i*0.01])
		rad *= 0.99

	#for i in range(90):
	#	x = math.sin( math.radians(i*4*4) ) * rad
	#	y = math.cos( math.radians(i*4*4) ) * rad
	#	verts.append([x,y, i*0.025])
	#	rad *= 0.9


	for jj in range( len(verts)-1 ):
		edges.append( [jj, jj+1] )

	mesh = bpy.data.meshes.new(name='__poo__')
	mesh.from_pydata(verts, edges, faces)
	mesh.update()

	poo = ob = bpy.data.objects.new('__💩__', object_data=mesh)
	bpy.context.collection.objects.link( ob )
	bpy.context.view_layer.objects.active = ob
	ob.select_set(True)
	lattice_mods.append(ob)
	ob.parent = root

	skin = ob.modifiers.new(name='pasta.skin', type="SKIN")
	#https://blender.stackexchange.com/questions/153019/how-to-set-vertex-radius-when-using-skin-modifier
	mesh = ob.data
	rad = 0.7
	for vidx, vert in enumerate(mesh.vertices):
		if vidx < len(mesh.vertices) / 2:
			if rad > 0.001:
				rad -= uniform(0.0001, 0.001)

		radx = rad
		rady = rad

		if vidx < len(mesh.vertices) / 3:
			radx += uniform(0,rad/2)
			rady += uniform(0,rad/2)
		elif vidx < len(mesh.vertices) / 1.5:
			radx += uniform(0,rad/16)
			rady += uniform(0,rad/16)
			rad *= 0.995
		else:
			rad *= 0.98

		#vert.bevel_weight = bev
		svert = mesh.skin_vertices[0].data[vidx]
		svert.radius = (radx,rady)

	mesh.skin_vertices[0].data[0].use_root = True

	bpy.ops.object.modifier_apply(modifier=skin.name)

	subsurf = ob.modifiers.new(name='pasta.surf', type="SUBSURF")
	subsurf.levels = 1
	bpy.ops.object.modifier_apply(modifier=subsurf.name)

	mod = ob.modifiers.new(name='decimate', type="DECIMATE")
	mod.ratio = 0.06
	#mod.ratio = 0.12
	#mod.ratio = 0.2
	bpy.ops.object.modifier_apply(modifier=mod.name)

	mat = bpy.data.materials.new(name='poo')
	mat.use_nodes = False
	mat.diffuse_color = [uniform(0.3, 0.5),uniform(0.2, 0.3),uniform(0.05, 0.1), 1]
	#mat.diffuse_color = [uniform(0.3, 0.7),uniform(0.2, 0.6),uniform(0.05, 0.4), 1]
	ob.data.materials.append(mat)


	mod = ob.modifiers.new(name='cut', type="BOOLEAN")
	mod.object = mouth
	mod.solver = "FAST"
	bpy.ops.object.modifier_apply(modifier=mod.name)
	for poly in poo.data.polygons[-32:]:
		poly.material_index = 3

	bobs = []

	R = 0.5
	r = uniform(0.3,0.5)
	if nurb_eyes:
		bpy.ops.surface.primitive_nurbs_surface_sphere_add()
	else:
		bpy.ops.mesh.primitive_ico_sphere_add()

	o = bpy.context.active_object
	o.location.x = 0.5
	o.location.y = -0.8
	o.location.z = 1.4
	o.parent = ob
	#o.scale.x = uniform(1,1.6)
	o.scale.x = uniform(0.7,1)
	o.scale.z = uniform(1,1.4)
	o.scale *= r
	o.scale.y = 0.4
	#bobs.append(o)
	lattice_mods.append(o)
	rig['EYES'].append(o)
	o.data.materials.append(wmat)

	wave = o.modifiers.new(name='wave', type="WAVE")
	wave.height = uniform(0.01, 0.05)
	wave.use_normal = True
	wave.speed = uniform(0.1, 0.2)


	if  nurb_eyes:
		bpy.ops.surface.primitive_nurbs_surface_sphere_add()
	else:
		bpy.ops.mesh.primitive_ico_sphere_add()

	e = bpy.context.active_object
	e.name = '👁'
	e.location.y = uniform(-1.2, -0.8)
	e.parent = o
	e.data.materials.append(bmat)
	e.scale *= 0.6
	lattice_mods.append(e)


	r = uniform(0.3,0.6)
	if nurb_eyes:
		bpy.ops.surface.primitive_nurbs_surface_sphere_add()
	else:
		bpy.ops.mesh.primitive_ico_sphere_add()

	o = bpy.context.active_object
	o.location.x = -0.4
	o.location.y = -0.7
	o.location.z = 1.5
	o.parent = ob
	#o.scale.x = uniform(1,1.3)
	o.scale.x = uniform(0.7,1)
	o.scale.z = uniform(1,1.1)
	o.scale *= r
	o.scale.y = 0.4
	#bobs.append(o)
	lattice_mods.append(o)
	rig['EYES'].append(o)
	o.data.materials.append(wmat)

	wave = o.modifiers.new(name='wave', type="WAVE")
	wave.height = uniform(0.1, 0.2)
	wave.use_normal = True
	wave.speed = uniform(0.01, 0.1)

	if nurb_eyes:
		bpy.ops.surface.primitive_nurbs_surface_sphere_add()
	else:
		bpy.ops.mesh.primitive_ico_sphere_add()

	e = bpy.context.active_object
	e.name = '👁'
	e.location.y = uniform(-1.2, -0.8)
	e.parent = o
	e.data.materials.append(bmat)
	e.scale *= 0.6
	lattice_mods.append(e)


	bpy.ops.object.add(type="LATTICE")
	lat = bpy.context.active_object
	lat.scale *= 3
	lat.data.points_u = 8
	lat.data.points_w = 8
	lat.location = [0,-1, 0.5]
	lat.parent = root
	lat.hide_viewport = True

	for pnt in lat.data.points:
		x,y,z = pnt.co_deform
		if abs(x) < 0.3 and y < 0 and abs(z) < 0.3:
			#pnt.co_deform *= 0.0
			pnt.co_deform.x *= 0.3
			pnt.co_deform.z *= 0.1
			#pnt.co_deform.y *= 0.0


	mod = ob.modifiers.new(name='lat', type="LATTICE")
	mod.object = lat

	lmod = lat.modifiers.new(name='def', type="SIMPLE_DEFORM")
	lmod.deform_method = "STRETCH"
	lmod.factor = uniform(0.2, 0.5)
	rig['STRETCH_MOUTH'] = lmod

	lmod = lat.modifiers.new(name='cast', type="CAST")
	lmod.use_y = False
	lmod.use_z = False
	lmod.factor = uniform(-1,1)
	lmod.radius = 0.6
	rig['CAST_MOUTH'] = lmod

	bpy.ops.object.add(type="LATTICE")
	lat = bpy.context.active_object
	lat.scale *= 4
	lat.data.points_u = 4
	lat.data.points_v = 4
	lat.data.points_w = 4
	lat.location = [0,0, 0.5]
	lat.parent = root
	lat.hide_viewport = True


	for o in lattice_mods:
		mod = o.modifiers.new(name='latall', type="LATTICE")
		mod.object = lat

	lmod = lat.modifiers.new(name='def', type="SIMPLE_DEFORM")
	lmod.deform_method = "BEND"
	lmod.angle = math.radians(uniform(45,90))
	rig['BEND'] = lmod

	lmod = lat.modifiers.new(name='def', type="SIMPLE_DEFORM")
	lmod.deform_method = "TWIST"
	lmod.angle = math.radians(uniform(-90,90))
	lmod.deform_axis = "Z"

	rig['TWIST'] = lmod

	lmod = lat.modifiers.new(name='def', type="SIMPLE_DEFORM")
	lmod.deform_method = "STRETCH"
	lmod.angle = 0
	lmod.deform_axis = "Z"
	rig['STRETCH'] = lmod


	## TODO not yet allowed for lattices
	#bpy.ops.object.modifier_apply_as_shapekey(keep_modifier=True, modifier=lmod.name)

	wave = ob.modifiers.new(name='wave', type="WAVE")
	wave.height = uniform(0.02, 0.1)
	wave.use_normal = True
	wave.speed = 0.1

	rig['WAVE'] = wave
	rig['mesh'] = ob

	for ob in lattice_mods:
		ob.select_set(True)

	bpy.context.view_layer.objects.active = poo
	bpy.ops.object.join()

	poo.data.materials.append(mmat)
	bpy.data.objects.remove( mouth )


	lipmat = bpy.data.materials.new(name='mouth')
	lipmat.use_nodes = False
	lipmat.diffuse_color = [random()*0.2,0,0, 1]
	lipmat.zigzag_object_type = "LOWER_LIP"
	poo.data.materials.append(lipmat)

	for poly in poo.data.polygons:
		if poly.material_index == 0 and poly.select:
			if poly.normal.z > 0.8:
				poly.material_index = 4
			else:
				poly.material_index = 3

	bpy.ops.mesh.primitive_cube_add()
	cu = bpy.context.active_object
	cu.scale *= 0.1
	cu.location = [-0.86, -1.18, 0.54]
	mod = cu.modifiers.new(name='array',type='ARRAY')
	mod.relative_offset_displace[0] = 1.2
	mod.count=8
	bpy.ops.object.modifier_apply(modifier='array')

	mod = cu.modifiers.new(name='array',type='ARRAY')
	mod.relative_offset_displace[0] = 0
	mod.relative_offset_displace[1] = 0.5
	mod.relative_offset_displace[2] = -1.2
	bpy.ops.object.modifier_apply(modifier='array')

	cu.data.materials.append(wmat)

	bpy.context.view_layer.objects.active = poo
	poo.select_set(True)
	cu.select_set(True)
	bpy.ops.object.join()

	for v in bpy.context.active_object.data.vertices:
		v.co *= 0.7
		v.co.z -= 0.3

	mod = poo.modifiers.new(name='quads', type="TRIANGULATE")
	mod.min_vertices = 5
	bpy.ops.object.modifier_apply(modifier=mod.name)


	return poo



