if __name__=='__main__':
	out = None
	for arg in sys.argv:
		if arg.startswith('--out='):
			out = arg.split('=')[-1]
		elif arg.startswith('--generate='):
			sym = arg.split('=')[-1]
			if sym in sym_gen:
				sym_gen[sym]()
			else:
				globals()[sym]()
	if out:
		if 'Cube' in bpy.data.objects:
			bpy.data.objects.remove( bpy.data.objects['Cube'] )
		bpy.ops.wm.save_as_mainfile(filepath=out)

